// ShowItCloser
// https://github.com/lotinerik/showitcloser

(function($){$.fn.showItCloser=function(d){var e=$.extend({maxWidth:"9999px",maxHeight:"9999px"},d);$(this).each(function(){var c=Math.floor(Math.random()*100000000)+Math.floor(Math.random()*100000000);$(this).addClass('showitcloser_link');$(this).wrap('<div id="showitcloser_wrapper_'+c+'" class="showitcloser_wrapper" style="position:relative; display:inline-block"></div>');$('#showitcloser_wrapper_'+c).bind('mouseover',function(){var a=$(this).find('.showitcloser_img');if($(a).length){$(a).show()}else{var b=$(this).find('.showitcloser_link').attr('href');$(this).append('<img src="'+b+'" class="showitcloser_img" style="max-width:'+e.maxWidth+'; max-height:'+e.maxHeight+'; z-index:10; position:absolute; top:0px; left:0px;" onload="showitcloser_posImg(this)" />')}});$('#showitcloser_wrapper_'+c).bind('mouseout',function(){$(this).find('.showitcloser_img').hide()})})}})(jQuery);function showitcloser_posImg(a){var b=$(a).parent().find('.showitcloser_link');var c=$(b).width();var d=$(b).height();var e=$(a).width();var f=$(a).height();if(e>c){$(a).css('left','-'+parseInt((e-c)/2)+'px')}if(f>d){$(a).css('top','-'+parseInt((f-d)/2)+'px')}}
